import React, { useState, useRef, useEffect, useCallback } from 'react';
import './App.css';
import DrawingCanvas from './DrawingCanvas';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEraser, faForward, faRedo } from '@fortawesome/free-solid-svg-icons';
import OpenAI from "openai";

// OpenAI設定
const openai = new OpenAI({
  apiKey: process.env.REACT_APP_OPENAI_API_KEY,
  dangerouslyAllowBrowser: true // クライアントサイドでAPIを使用する場合に必要
});

function App() {
  const [characters, setCharacters] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [stage, setStage] = useState(0);
  const [showHint, setShowHint] = useState(false);
  const [readings, setReadings] = useState({ onYomi: '', kunYomi: '' });
  const drawingCanvasRef = useRef();

  useEffect(() => {
    fetch('/Mac_19_Hitu_Dictionary.txt')
      .then(response => response.text())
      .then(text => {
        const lines = text.split('\n');
        const parsed = lines.map(line => {
          const match = line.match(/"([^"]+)","([^"]+)","[^"]+"/);
          if (match) {
            const [, char, componentsStr] = match;
            const components = componentsStr.split('').slice(1);
            return { char, components };
          }
          return null;
        }).filter(Boolean);
        setCharacters(parsed);
        setCurrentIndex(Math.floor(Math.random() * parsed.length));
      });
  }, []);

  const getReadings = useCallback(async (char) => {
    try {
      const response = await openai.chat.completions.create({
        model: process.env.REACT_APP_OPENAI_MODEL,
        messages: [
          {
            role: "system",
            content: "あなたは日本語の専門家です。漢字の読み方について質問されます。"
          },
          {
            role: "user",
            content: `日本語の漢字「${char}」の音読みと訓読みを教えてください。回答は以下の形式でお願いします：
音読み：（音読みをカタカナで）
訓読み：（訓読みをひらがなで）`
          }
        ],
        max_tokens: 100,
      });

      const text = response.choices[0].message.content.trim();
      const onYomi = text.match(/音読み：(.+)/)?.[1] || '';
      const kunYomi = text.match(/訓読み：(.+)/)?.[1] || '';

      setReadings({ onYomi, kunYomi });
    } catch (error) {
      console.error("Error fetching readings:", error);
      setReadings({ onYomi: 'エラー', kunYomi: 'エラー' });
    }
  }, []);

  useEffect(() => {
    if (characters[currentIndex]) {
      getReadings(characters[currentIndex].char);
    }
  }, [currentIndex, characters, getReadings]);

  const handleNext = () => {
    const currentChar = characters[currentIndex];
    if (currentChar) {
      setStage(prevStage => {
        const nextStage = prevStage + 1;
        return nextStage > currentChar.components.length ? 0 : nextStage;
      });
    }
  };

  const handleClear = () => {
    if (drawingCanvasRef.current) {
      drawingCanvasRef.current.clearAndRedraw();
    }
  };

  const handleNextCharacter = useCallback(() => {
    setCurrentIndex(prevIndex => {
      let newIndex;
      do {
        newIndex = Math.floor(Math.random() * characters.length);
      } while (newIndex === prevIndex && characters.length > 1);
      return newIndex;
    });
    setStage(0);
    setShowHint(false);
  }, [characters]);

  const toggleHint = () => {
    setShowHint(prev => !prev);
  };

  const currentChar = characters[currentIndex] || { char: '', components: [] };

  return (
    <div className="App">
      <header className="App-header">
        <h1>UDHituAStd 漢字学習アプリ</h1>
      </header>
      <main>
        <div className="character-info">
          <h2>現在の文字: <span className="udhitu-font">{currentChar.char}</span></h2>
          <p>画数: {currentChar.components.length}</p>
          <p>音読み: <span className="reading">{readings.onYomi}</span></p>
          <p>訓読み: <span className="reading">{readings.kunYomi}</span></p>
          <button onClick={toggleHint} className="hint-button">
            {showHint ? 'ヒントを隠す' : 'ヒントを表示'}
          </button>
          {showHint && (
            <div className="hint">
              <p>構成要素: <span className="udhitu-font">{currentChar.components.join(' ')}</span></p>
            </div>
          )}
        </div>
        <DrawingCanvas 
          ref={drawingCanvasRef} 
          stage={stage} 
          character={currentChar.char} 
          components={currentChar.components}
        />
        <div className="controls">
          <button onClick={handleClear} className="control-button">
            <FontAwesomeIcon icon={faEraser} /> クリア
          </button>
          <button onClick={handleNext} className="control-button">
            <FontAwesomeIcon icon={faForward} /> 次へ
          </button>
          <button onClick={handleNextCharacter} className="control-button">
            <FontAwesomeIcon icon={faRedo} /> 次の文字
          </button>
        </div>
      </main>
    </div>
  );
}

export default App;
