import React, { useState, useRef } from 'react';
import './App.css';
import DrawingCanvas from './DrawingCanvas';
import ControlButtons from './ControlButtons';

function App() {
  const [stage, setStage] = useState(0);
  const drawingCanvasRef = useRef();

  const handleNext = () => {
    setStage((prevStage) => (prevStage + 1) % 3); // 0, 1, 2 の3段階
  };

  const handleClear = () => {
    if (drawingCanvasRef.current) {
      drawingCanvasRef.current.clearAndRedraw();
    }
  };

  const handleDraw = () => {
    console.log('Drawing...');
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>UDHituAStd 漢字学習アプリ</h1>
      </header>
      <main>
        <DrawingCanvas ref={drawingCanvasRef} stage={stage} onDraw={handleDraw} />
        <ControlButtons onNext={handleNext} onClear={handleClear} />
      </main>
    </div>
  );
}

export default App;
