import React, { useRef, useEffect, useState, useCallback } from 'react';
import './DrawingCanvas.css';

const DrawingCanvas = React.forwardRef(({ character, stage, maxStage, characterData }, ref) => {
  const canvasRef = useRef(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const [fontLoaded, setFontLoaded] = useState(false);
  const [currentCharacters, setCurrentCharacters] = useState([]);

  useEffect(() => {
    const loadFont = async () => {
      await document.fonts.load('120px UDHituAStd-E12');
      setFontLoaded(true);
    };
    loadFont();
  }, []);

  const shuffleCharacters = useCallback(() => {
    const chars = characterData[character] || [];
    const shuffled = [...chars].sort(() => Math.random() - 0.5);
    setCurrentCharacters(shuffled);
  }, [character, characterData]);

  useEffect(() => {
    if (fontLoaded && characterData[character]) {
      if (stage === 0) {
        setCurrentCharacters(characterData[character]);
      } else {
        shuffleCharacters();
      }
    }
  }, [character, stage, fontLoaded, shuffleCharacters, characterData]);

  useEffect(() => {
    if (fontLoaded) {
      clearAndRedraw();
    }
  }, [character, stage, fontLoaded, currentCharacters]);

  const clearAndRedraw = () => {
    const canvas = canvasRef.current;
    if (!canvas) return;
    const context = canvas.getContext('2d');
    context.clearRect(0, 0, canvas.width, canvas.height);
    drawCharacters(context);
  };

  const drawCharacters = (context) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    context.font = '120px UDHituAStd-E12';
    context.textAlign = 'center';
    context.textBaseline = 'middle';

    const centerX = canvas.width / 2;
    const centerY = canvas.height / 2;

    // まず、全ての文字をグレーで描画
    context.fillStyle = '#E0E0E0';
    currentCharacters.forEach(char => {
      context.fillText(char, centerX, centerY);
    });

    // 次に、現在のステージまでの文字を黒で描画
    context.fillStyle = '#000000';
    for (let i = 0; i < stage + 1 && i < currentCharacters.length; i++) {
      context.fillText(currentCharacters[i], centerX, centerY);
    }
  };

  const startDrawing = (e) => {
    const { offsetX, offsetY } = e.nativeEvent;
    setIsDrawing(true);
    const context = canvasRef.current.getContext('2d');
    context.beginPath();
    context.moveTo(offsetX, offsetY);
    context.strokeStyle = '#000000';
    context.lineWidth = 2;
  };

  const draw = (e) => {
    if (!isDrawing) return;
    const { offsetX, offsetY } = e.nativeEvent;
    const context = canvasRef.current.getContext('2d');
    context.lineTo(offsetX, offsetY);
    context.stroke();
  };

  const stopDrawing = () => {
    setIsDrawing(false);
  };

  React.useImperativeHandle(ref, () => ({
    clearAndRedraw
  }));

  return (
    <div className="drawing-canvas">
      <canvas
        ref={canvasRef}
        width={300}
        height={300}
        onMouseDown={startDrawing}
        onMouseMove={draw}
        onMouseUp={stopDrawing}
        onMouseOut={stopDrawing}
      />
    </div>
  );
});

export default DrawingCanvas;
