import React, { useState, useRef, useEffect, useCallback } from 'react';
import './App.css';
import DrawingCanvas from './DrawingCanvas';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEraser, faForward, faRedo } from '@fortawesome/free-solid-svg-icons';

function App() {
  const [characters, setCharacters] = useState([]);
  const [strokeDictionary, setStrokeDictionary] = useState({});
  const [currentIndex, setCurrentIndex] = useState(0);
  const [stage, setStage] = useState(0);
  const [showHint, setShowHint] = useState(false);
  const drawingCanvasRef = useRef();

  useEffect(() => {
    Promise.all([
      fetch('/1nen_kanji.txt').then(response => response.text()),
      fetch('/Mac_19_Hitu_Dictionary.txt').then(response => response.text())
    ]).then(([kanjiText, dictionaryText]) => {
      // 1年生の漢字データを処理
      const kanjiLines = kanjiText.split('\n').slice(1);
      const parsed = kanjiLines.map(line => {
        const [char, strokes, onYomi, kunYomi] = line.split('\t');
        return { char, strokes: parseInt(strokes), onYomi, kunYomi };
      });
      setCharacters(parsed);

      // Mac_19_Hitu_Dictionary.txtのデータを処理
      const dictionaryLines = dictionaryText.split('\n');
      const strokeDict = {};
      dictionaryLines.forEach(line => {
        const match = line.match(/"([^"]+)","([^"]+)","[^"]+"/);
        if (match) {
          const [, char, componentsStr] = match;
          strokeDict[char] = componentsStr.split('').slice(1);
        }
      });
      setStrokeDictionary(strokeDict);

      // 最初の文字をセット
      setCurrentIndex(Math.floor(Math.random() * parsed.length));
    });
  }, []);

  const handleNext = () => {
    const currentChar = characters[currentIndex];
    if (currentChar) {
      setStage(prevStage => {
        const nextStage = prevStage + 1;
        return nextStage > (strokeDictionary[currentChar.char]?.length || 0) ? 0 : nextStage;
      });
    }
  };

  const handleClear = () => {
    if (drawingCanvasRef.current) {
      drawingCanvasRef.current.clearAndRedraw();
    }
  };

  const handleNextCharacter = useCallback(() => {
    setCurrentIndex(prevIndex => {
      let newIndex;
      do {
        newIndex = Math.floor(Math.random() * characters.length);
      } while (newIndex === prevIndex && characters.length > 1);
      return newIndex;
    });
    setStage(0);
    setShowHint(false);
  }, [characters]);

  const toggleHint = () => {
    setShowHint(prev => !prev);
  };

  const currentChar = characters[currentIndex] || { char: '', strokes: 0, onYomi: '', kunYomi: '' };
  const components = strokeDictionary[currentChar.char] || currentChar.char.split('');

  return (
    <div className="App">
      <header className="App-header">
        <h1>UDHituAStd 漢字学習アプリ</h1>
      </header>
      <main>
        <div className="character-info">
          <h2>現在の文字: <span className="udhitu-font">{currentChar.char}</span></h2>
          <p>音読み: {currentChar.onYomi} / 訓読み: {currentChar.kunYomi}</p>
          <p>画数: {components.length}</p>
          <button onClick={toggleHint} className="hint-button">
            {showHint ? 'ヒントを隠す' : 'ヒントを表示'}
          </button>
          {showHint && (
            <div className="hint">
              <p>構成要素: <span className="udhitu-font">{components.join(' ')}</span></p>
            </div>
          )}
        </div>
        <DrawingCanvas 
          ref={drawingCanvasRef} 
          stage={stage} 
          character={currentChar.char} 
          components={components}
        />
        <div className="controls">
          <button onClick={handleClear} className="control-button">
            <FontAwesomeIcon icon={faEraser} /> クリア
          </button>
          <button onClick={handleNext} className="control-button">
            <FontAwesomeIcon icon={faForward} /> 次へ
          </button>
          <button onClick={handleNextCharacter} className="control-button">
            <FontAwesomeIcon icon={faRedo} /> 次の文字
          </button>
        </div>
      </main>
    </div>
  );
}

export default App;
