import React, { useState, useEffect, useCallback } from 'react';
import './App.css';
import DrawingCanvas from './DrawingCanvas';
import ControlButtons from './ControlButtons';
import { loadCharacterData, getRandomCharacter, getStrokeCount } from './characterData';

function App() {
  const [currentChar, setCurrentChar] = useState('');
  const [reading, setReading] = useState('');
  const [currentStroke, setCurrentStroke] = useState(1);
  const [totalStrokes, setTotalStrokes] = useState(1);
  const [characterData, setCharacterData] = useState({});

  useEffect(() => {
    const loadData = async () => {
      const data = await loadCharacterData();
      setCharacterData(data);
      selectNewCharacter(data);
    };
    loadData();
  }, []);

  const selectNewCharacter = useCallback((data) => {
    const { character, reading } = getRandomCharacter(data);
    setCurrentChar(character);
    setReading(reading);
    const strokes = getStrokeCount(character, data);
    setTotalStrokes(strokes);
    setCurrentStroke(1);
  }, []);

  const handleNext = useCallback(() => {
    if (currentStroke < totalStrokes) {
      setCurrentStroke(prev => prev + 1);
    } else {
      selectNewCharacter(characterData);
    }
  }, [currentStroke, totalStrokes, characterData, selectNewCharacter]);

  const handleClear = useCallback(() => {
    // DrawingCanvasのclear機能を呼び出す
  }, []);

  const handleNextCharacter = useCallback(() => {
    selectNewCharacter(characterData);
  }, [characterData, selectNewCharacter]);

  return (
    <div className="App">
      <header className="App-header">
        <h1>UDHituAStd 漢字学習アプリ</h1>
      </header>
      <main>
        <div className="character-display">
          <span className="udihitu-font">{currentChar}</span>
          <p>文字: {currentChar}（{reading}）</p>
          <p>画数: {currentStroke}/{totalStrokes}</p>
        </div>
        <DrawingCanvas />
        <ControlButtons 
          onNext={handleNext} 
          onClear={handleClear} 
          onNextCharacter={handleNextCharacter}
        />
      </main>
    </div>
  );
}

export default App;
