import React, { useState } from 'react';
import './App.css';

function App() {
  const [currentChar, setCurrentChar] = useState('あ');
  const [stage, setStage] = useState(0);

  const handleNext = () => {
    // ステージを進める処理
    setStage((prevStage) => (prevStage + 1) % 5);
  };

  const handleReset = () => {
    // リセット処理
    setStage(0);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>UDHituAStd 漢字学習アプリ</h1>
      </header>
      <main>
        <div className="character-display">
          {/* ここに文字表示コンポーネントを配置 */}
          <p style={{ fontSize: '100px' }}>{currentChar}</p>
        </div>
        <div className="drawing-canvas">
          {/* ここに描画キャンバスコンポーネントを配置 */}
          <p>描画エリア（実装予定）</p>
        </div>
        <div className="controls">
          <button onClick={handleNext}>次へ</button>
          <button onClick={handleReset}>リセット</button>
        </div>
      </main>
    </div>
  );
}

export default App;
