import React, { useState, useRef, useEffect } from 'react';
import './App.css';
import DrawingCanvas from './DrawingCanvas';
import ControlButtons from './ControlButtons';
import { loadCharacterData, getRandomCharacter, getStrokeCount } from './characterData';

function App() {
  const [currentChar, setCurrentChar] = useState('');
  const [stage, setStage] = useState(0);
  const [maxStage, setMaxStage] = useState(1);
  const [characterData, setCharacterData] = useState({});
  const drawingCanvasRef = useRef();

  useEffect(() => {
    const loadData = async () => {
      const data = await loadCharacterData();
      setCharacterData(data);
      const initialChar = getRandomCharacter(data);
      setCurrentChar(initialChar);
      setMaxStage(getStrokeCount(initialChar, data));
    };
    loadData();
  }, []);

  const handleNext = () => {
    setStage((prevStage) => {
      const nextStage = (prevStage + 1) % maxStage;
      console.log(`Stage updated: ${prevStage} -> ${nextStage}`);
      return nextStage;
    });
  };

  const handleClear = () => {
    if (drawingCanvasRef.current) {
      drawingCanvasRef.current.clearAndRedraw();
    }
  };

  const handleNextCharacter = () => {
    const newChar = getRandomCharacter(characterData);
    setCurrentChar(newChar);
    const newMaxStage = getStrokeCount(newChar, characterData);
    setMaxStage(newMaxStage);
    setStage(0);
    console.log(`New character: ${newChar}, Max stage: ${newMaxStage}`);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>UDHituAStd 漢字学習アプリ</h1>
      </header>
      <main>
        <DrawingCanvas 
          ref={drawingCanvasRef} 
          character={currentChar} 
          stage={stage} 
          maxStage={maxStage}
          characterData={characterData}
        />
        <ControlButtons 
          onNext={handleNext} 
          onClear={handleClear} 
          onNextCharacter={handleNextCharacter}
        />
        <div>Debug: Char: {currentChar}, Stage: {stage}/{maxStage}</div>
      </main>
    </div>
  );
}

export default App;
