import React, { useState, useRef, useEffect, useCallback } from 'react';
import './App.css';
import DrawingCanvas from './DrawingCanvas';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEraser, faForward, faRedo, faCheck } from '@fortawesome/free-solid-svg-icons';

function App() {
  const [characters, setCharacters] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [stage, setStage] = useState(0);
  const [showHint, setShowHint] = useState(false);
  const [gptResponse, setGptResponse] = useState('');
  const [llmJudgement, setLlmJudgement] = useState('');
  const drawingCanvasRef = useRef();

  useEffect(() => {
    fetch('/Mac_19_Hitu_Dictionary.txt')
      .then(response => response.text())
      .then(text => {
        const lines = text.split('\n');
        const parsed = lines.map(line => {
          const match = line.match(/"([^"]+)","([^"]+)","[^"]+"/);
          if (match) {
            const [, char, componentsStr] = match;
            const components = componentsStr.split('').slice(1);
            return { char, components };
          }
          return null;
        }).filter(Boolean);
        setCharacters(parsed);
        setCurrentIndex(Math.floor(Math.random() * parsed.length));
      });
  }, []);

  const getReadings = useCallback(async (char) => {
    try {
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`
        },
        body: JSON.stringify({
          model: 'gpt-4o',
          messages: [
            {
              role: "system",
              content: "あなたは日本語の専門家です。漢字の読み方について質問されます。"
            },
            {
              role: "user",
              content: `日本語の漢字「${char}」の音読みと訓読みを教えてください。回答は余計な言葉を加えず「音読み：** / 訓読み：**」と答えて。`
            }
          ],
          max_tokens: 100,
        })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      const text = data.choices[0].message.content.trim();
      setGptResponse(text);
    } catch (error) {
      console.error("Error fetching readings:", error);
      setGptResponse('エラーが発生しました。');
    }
  }, []);

  const judgeDrawing = useCallback(async () => {
    if (!drawingCanvasRef.current) return;

    const canvas = drawingCanvasRef.current.getCanvas();
    const imageDataUrl = canvas.toDataURL('image/png');

    try {
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`
        },
        body: JSON.stringify({
          model: 'gpt-4-vision-preview',
          messages: [
            {
              role: "user",
              content: [
                {
                  type: "text",
                  text: `この画像に書かれている文字は「${characters[currentIndex].char}」として正しく書けていますか？正しければ「はい」、間違っていれば「いいえ」と答え、その理由を簡潔に説明してください。`
                },
                {
                  type: "image_url",
                  image_url: {
                    url: imageDataUrl
                  }
                }
              ]
            }
          ],
          max_tokens: 300,
        })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      const judgement = data.choices[0].message.content.trim();
      setLlmJudgement(judgement);
    } catch (error) {
      console.error("Error judging drawing:", error);
      setLlmJudgement('判断中にエラーが発生しました。');
    }
  }, [characters, currentIndex]);

  useEffect(() => {
    if (characters[currentIndex]) {
      getReadings(characters[currentIndex].char);
    }
  }, [currentIndex, characters, getReadings]);

  const handleNext = () => {
    const currentChar = characters[currentIndex];
    if (currentChar) {
      setStage(prevStage => {
        const nextStage = prevStage + 1;
        return nextStage > currentChar.components.length ? 0 : nextStage;
      });
    }
  };

  const handleClear = () => {
    if (drawingCanvasRef.current) {
      drawingCanvasRef.current.clearAndRedraw();
    }
    setLlmJudgement('');
  };

  const handleNextCharacter = useCallback(() => {
    setCurrentIndex(prevIndex => {
      let newIndex;
      do {
        newIndex = Math.floor(Math.random() * characters.length);
      } while (newIndex === prevIndex && characters.length > 1);
      return newIndex;
    });
    setStage(0);
    setShowHint(false);
    setLlmJudgement('');
  }, [characters]);

  const toggleHint = () => {
    setShowHint(prev => !prev);
  };

  const currentChar = characters[currentIndex] || { char: '', components: [] };

  return (
    <div className="App">
      <header className="App-header">
        <h1>UDHituAStd 漢字学習アプリ</h1>
      </header>
      <main>
        <div className="character-info">
          <h2>
            <span className="gpt-response">{gptResponse}</span>
          </h2>
          <p>画数: {currentChar.components.length}</p>
          <button onClick={toggleHint} className="hint-button">
            {showHint ? 'ヒントを隠す' : 'ヒントを表示'}
          </button>
          {showHint && (
            <div className="hint">
              <p>構成要素: <span className="udhitu-font">{currentChar.components.join(' ')}</span></p>
            </div>
          )}
        </div>
        <DrawingCanvas 
          ref={drawingCanvasRef} 
          stage={stage} 
          character={currentChar.char} 
          components={currentChar.components}
        />
        <div className="controls">
          <button onClick={handleClear} className="control-button">
            <FontAwesomeIcon icon={faEraser} /> クリア
          </button>
          <button onClick={handleNext} className="control-button">
            <FontAwesomeIcon icon={faForward} /> 次へ
          </button>
          <button onClick={handleNextCharacter} className="control-button">
            <FontAwesomeIcon icon={faRedo} /> 次の文字
          </button>
          <button onClick={judgeDrawing} className="control-button">
            <FontAwesomeIcon icon={faCheck} /> 判定
          </button>
        </div>
        {llmJudgement && (
          <div className="llm-judgement">
            <h3>LLMの判定:</h3>
            <p>{llmJudgement}</p>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
