import React, { useState, useRef, useEffect, useCallback, useMemo } from 'react';
import './App.css';
import './fonts.css';  // 新しく追加
import DrawingCanvas from './DrawingCanvas';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEraser, faForward, faRedo, faCheck } from '@fortawesome/free-solid-svg-icons';

function App() {
  const [kanjiList, setKanjiList] = useState([]);
  const [components, setComponents] = useState({});
  const [currentIndex, setCurrentIndex] = useState(0);
  const [stage, setStage] = useState(0);
  const [showHint, setShowHint] = useState(false);
  const [llmJudgement, setLlmJudgement] = useState('');
  const [error, setError] = useState(null);
  const drawingCanvasRef = useRef();

  useEffect(() => {
    const loadData = async () => {
      try {
        const [kanjiResponse, componentsResponse] = await Promise.all([
          fetch('/1nen_kanji.txt'),
          fetch('/Mac_19_Hitu_Dictionary.txt')
        ]);

        const kanjiText = await kanjiResponse.text();
        const componentsText = await componentsResponse.text();

        const parsedKanji = kanjiText.split('\n').map(line => {
          const [kanji, strokes, onYomi, kunYomi] = line.split('\t');
          return { kanji, strokes: parseInt(strokes), onYomi, kunYomi };
        });

        const parsedComponents = {};
        componentsText.split('\n').forEach(line => {
          const match = line.match(/"([^"]+)","([^"]+)","[^"]+"/);
          if (match) {
            const [, kanji, componentsStr] = match;
            parsedComponents[kanji] = componentsStr.split('').filter(char => char !== ' ');
          }
        });

        setKanjiList(parsedKanji);
        setComponents(parsedComponents);
        setCurrentIndex(Math.floor(Math.random() * parsedKanji.length));
      } catch (error) {
        console.error("Error loading data:", error);
        setError("データの読み込み中にエラーが発生しました。");
      }
    };

    loadData();
  }, []);

  const currentKanji = useMemo(() => kanjiList[currentIndex] || { kanji: '', strokes: 0, onYomi: '', kunYomi: '' }, [kanjiList, currentIndex]);
  const currentComponents = useMemo(() => components[currentKanji.kanji] || [], [components, currentKanji.kanji]);

  const handleNext = useCallback(() => {
    if (currentComponents.length > 0) {
      setStage(prevStage => {
        const nextStage = prevStage + 1;
        return nextStage > currentComponents.length ? 0 : nextStage;
      });
    }
  }, [currentComponents]);

  const handleClear = useCallback(() => {
    if (drawingCanvasRef.current) {
      drawingCanvasRef.current.clearAndRedraw();
    }
    setLlmJudgement('');
  }, []);

  const handleNextCharacter = useCallback(() => {
    if (kanjiList.length > 1) {
      setCurrentIndex(prevIndex => {
        let newIndex;
        do {
          newIndex = Math.floor(Math.random() * kanjiList.length);
        } while (newIndex === prevIndex);
        return newIndex;
      });
    }
    setStage(0);
    setShowHint(false);
    setLlmJudgement('');
  }, [kanjiList]);

  const toggleHint = useCallback(() => {
    setShowHint(prev => !prev);
  }, []);

  const judgeDrawing = useCallback(async () => {
    if (!drawingCanvasRef.current) return;

    const canvas = drawingCanvasRef.current.getCanvas();
    const imageData = canvas.toDataURL('image/png');
    const base64Data = imageData.split(',')[1];

    try {
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`
        },
        body: JSON.stringify({
          model: 'gpt-4o',
          messages: [
            {
              role: "user",
              content: [
                {
                  type: "text",
                  text: `この画像に書かれている文字は「${currentKanji.kanji}」として正しく書けていますか？正しさを4段階で評価してください。評価："正しい。さすが！"、"少し違うね。もうちょっと丁寧に！"、"よく見直して！違う箇所があるよ"、"もう一度お手本を確認してね"`
                },
                {
                  type: "image_url",
                  image_url: {
                    url: `data:image/png;base64,${base64Data}`
                  }
                }
              ]
            }
          ],
          max_tokens: 300,
        })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setLlmJudgement(data.choices[0].message.content.trim());
    } catch (error) {
      console.error("Error judging drawing:", error);
      setLlmJudgement('判断中にエラーが発生しました。');
    }
  }, [currentKanji.kanji]);

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>UDHituAStd 漢字学習アプリ</h1>
      </header>
      <main>
        <div className="character-info">
          <h2>
            <div>音読み：{currentKanji.onYomi} / 訓読み：{currentKanji.kunYomi}</div>
            <div>画数: {currentKanji.strokes}</div>
          </h2>
          <button onClick={toggleHint} className="hint-button">
            {showHint ? 'ヒントを隠す' : 'ヒントを表示'}
          </button>
          {showHint && (
            <div className="hint">
              <p>構成要素: <span className="udhitu-font">{currentComponents.join(' ')}</span></p>
            </div>
          )}
        </div>
        <DrawingCanvas 
          ref={drawingCanvasRef} 
          stage={stage} 
          character={currentKanji.kanji} 
          components={currentComponents}
        />
        <div className="controls">
          <button onClick={handleClear} className="control-button">
            <FontAwesomeIcon icon={faEraser} /> クリア
          </button>
          <button onClick={handleNext} className="control-button">
            <FontAwesomeIcon icon={faForward} /> 次へ
          </button>
          <button onClick={handleNextCharacter} className="control-button">
            <FontAwesomeIcon icon={faRedo} /> 次の文字
          </button>
          <button onClick={judgeDrawing} className="control-button">
            <FontAwesomeIcon icon={faCheck} /> 判定
          </button>
        </div>
        {llmJudgement && (
          <div className="llm-judgement">
            <h3>LLMの判定:</h3>
            <p>{llmJudgement}</p>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
