import React, { useState, useRef, useEffect } from 'react';
import './App.css';
import DrawingCanvas from './DrawingCanvas';
import ControlButtons from './ControlButtons';
import { loadCharacterData, getRandomCharacter, getStrokeCount } from './characterData';

function App() {
  const [currentChar, setCurrentChar] = useState('');
  const [stage, setStage] = useState(0);
  const [characterData, setCharacterData] = useState({});
  const drawingCanvasRef = useRef();

  useEffect(() => {
    const loadData = async () => {
      const data = await loadCharacterData();
      setCharacterData(data);
      const initialChar = getRandomCharacter(data);
      setCurrentChar(initialChar);
    };
    loadData();
  }, []);

  const handleNext = () => {
    const maxStage = getStrokeCount(currentChar, characterData);
    setStage((prevStage) => (prevStage + 1) % maxStage);
  };

  const handleClear = () => {
    if (drawingCanvasRef.current) {
      drawingCanvasRef.current.clearCanvas();
    }
  };

  const handleNextCharacter = () => {
    const newChar = getRandomCharacter(characterData);
    setCurrentChar(newChar);
    setStage(0);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>UDHituAStd 漢字学習アプリ</h1>
      </header>
      <main>
        <DrawingCanvas 
          ref={drawingCanvasRef} 
          character={currentChar} 
          stage={stage}
          characterData={characterData}
        />
        <ControlButtons 
          onNext={handleNext} 
          onClear={handleClear} 
          onNextCharacter={handleNextCharacter}
        />
      </main>
    </div>
  );
}

export default App;
