import React, { useEffect, useState } from 'react';

const SVGCharacterDisplay = ({ character, stage, characterData, style }) => {
  const [paths, setPaths] = useState([]);

  useEffect(() => {
    console.log('SVGCharacterDisplay updated:', { character, stage, characterData });
    if (characterData[character]) {
      const chars = characterData[character];
      const newPaths = chars.map((char, index) => ({
        d: `M150,150 m0,0 l50,50`, // This is still a placeholder path
        stroke: index < stage ? 'black' : '#E0E0E0',
        strokeWidth: 10,
        strokeLinecap: 'round',
        fill: 'none'
      }));
      setPaths(newPaths);
    } else {
      console.log('No character data found for:', character);
    }
  }, [character, stage, characterData]);

  return (
    <>
      <svg width="300" height="300" viewBox="0 0 300 300" style={style}>
        {paths.map((path, index) => (
          <path key={index} {...path} />
        ))}
      </svg>
      <div style={{ position: 'absolute', top: 0, left: 0, background: 'rgba(255,255,255,0.8)' }}>
        <p>Character: {character}</p>
        <p>Stage: {stage}</p>
        <p>Paths: {paths.length}</p>
      </div>
    </>
  );
};

export default SVGCharacterDisplay;
