import React, { useState } from 'react';
import './App.css';
import CharacterDisplay from './CharacterDisplay';
import DrawingCanvas from './DrawingCanvas';
import ControlButtons from './ControlButtons';

function App() {
  const [currentChar] = useState('あ');
  const [stage, setStage] = useState(0);

  const handleNext = () => {
    setStage((prevStage) => (prevStage + 1) % 4); // 0, 1, 2, 3 の4段階
  };

  const handleReset = () => {
    setStage(0);
  };

  const handleDraw = () => {
    // ここに描画時の処理を追加できます（例：正誤判定など）
    console.log('Drawing...');
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>UDHituAStd 漢字学習アプリ</h1>
      </header>
      <main>
        <CharacterDisplay character={currentChar} stage={stage} />
        <DrawingCanvas onDraw={handleDraw} />
        <ControlButtons onNext={handleNext} onReset={handleReset} />
      </main>
    </div>
  );
}

export default App;
