import React, { useState, useRef, useEffect, useCallback } from 'react';
import './App.css';
import DrawingCanvas from './DrawingCanvas';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEraser, faForward, faRedo } from '@fortawesome/free-solid-svg-icons';

function App() {
  const [characters, setCharacters] = useState([]);
  const [hituDictionary, setHituDictionary] = useState({});
  const [currentKanji, setCurrentKanji] = useState(null);
  const [components, setComponents] = useState([]);
  const [stage, setStage] = useState(0);
  const [showHint, setShowHint] = useState(false);
  const [refreshKey, setRefreshKey] = useState(0);
  const drawingCanvasRef = useRef();

  useEffect(() => {
    // 1年生の漢字データを読み込む
    fetch('/1nen_kanji.txt')
      .then(response => response.text())
      .then(text => {
        const lines = text.split('\n').slice(1); // ヘッダーを除外
        const parsed = lines.map(line => {
          const [char, strokes, onYomi, kunYomi] = line.split('\t');
          return { char, strokes: parseInt(strokes), onYomi, kunYomi };
        });
        setCharacters(parsed);
        // 初期の漢字をランダムに選択
        const initialKanji = parsed[Math.floor(Math.random() * parsed.length)];
        setCurrentKanji(initialKanji);
      });

    // Mac_19_Hitu_Dictionary.txtを読み込む
    fetch('/Mac_19_Hitu_Dictionary.txt')
      .then(response => response.text())
      .then(text => {
        const lines = text.split('\n');
        const dictionary = {};
        lines.forEach(line => {
          const match = line.match(/"([^"]+)","([^"]+)","[^"]+"/);
          if (match) {
            const [, char, componentsStr] = match;
            dictionary[char] = componentsStr.split('').slice(1);
          }
        });
        setHituDictionary(dictionary);
      });
  }, []);

  useEffect(() => {
    if (currentKanji && hituDictionary[currentKanji.char]) {
      setComponents(hituDictionary[currentKanji.char]);
      setStage(0);
      setRefreshKey(prevKey => prevKey + 1);
    }
  }, [currentKanji, hituDictionary]);

  const handleNext = useCallback(() => {
    if (currentKanji) {
      setStage(prevStage => {
        const nextStage = prevStage + 1;
        return nextStage > components.length ? 0 : nextStage;
      });
      setRefreshKey(prevKey => prevKey + 1);
    }
  }, [currentKanji, components.length]);

  const handleClear = useCallback(() => {
    if (drawingCanvasRef.current) {
      drawingCanvasRef.current.clearAndRedraw();
      setRefreshKey(prevKey => prevKey + 1);
    }
  }, []);

  const handleNextCharacter = useCallback(() => {
    const newKanji = characters[Math.floor(Math.random() * characters.length)];
    setCurrentKanji(newKanji);
    setStage(0);
    setShowHint(false);
    setRefreshKey(prevKey => prevKey + 1);
  }, [characters]);

  const toggleHint = () => {
    setShowHint(prev => !prev);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>UDHituAStd 漢字学習アプリ</h1>
      </header>
      <main>
        <div className="character-info">
          {currentKanji && (
            <>
              <h2>音読み: {currentKanji.onYomi} / 訓読み: {currentKanji.kunYomi}</h2>
              <p>画数: {currentKanji.strokes}</p>
            </>
          )}
          <button onClick={toggleHint} className="hint-button">
            {showHint ? 'ヒントを隠す' : 'ヒントを表示'}
          </button>
          {showHint && (
            <div className="hint">
              <p>構成要素: <span className="udhitu-font">{components.join(' ')}</span></p>
            </div>
          )}
        </div>
        <DrawingCanvas 
          ref={drawingCanvasRef} 
          stage={stage} 
          character={currentKanji ? currentKanji.char : ''}
          components={components}
          refreshKey={refreshKey}
        />
        <div className="controls">
          <button onClick={handleClear} className="control-button">
            <FontAwesomeIcon icon={faEraser} /> クリア
          </button>
          <button onClick={handleNext} className="control-button">
            <FontAwesomeIcon icon={faForward} /> 次へ
          </button>
          <button onClick={handleNextCharacter} className="control-button">
            <FontAwesomeIcon icon={faRedo} /> 次の文字
          </button>
        </div>
      </main>
    </div>
  );
}

export default App;
